const Loading = ({ isLoading }) => {
  if (!isLoading) {
    document.querySelector(".gallery-loading-container").classList.add("hide");
  }
  return (
    <div className="gallery-loading-container">
      <h1>LOADING...</h1>
    </div>
  );
};

export default Loading;
