import React, { useState, useEffect } from "react";
import "../css/home.css";

const Home = (props) => {
  const [imageOfTheDay, setImageOfTheDay] = useState({
    url: "/images/stars.jpg",
  });
  const [headerIsLoaded, setHeaderIsLoaded] = useState(true);
  /*
  useEffect(() => {
    if (headerIsLoaded) {
      if (imageOfTheDay === "/images/stars.jpg") {
      }
      setTimeout(() => {
        document
          .querySelector(".loading-screen--container")
          .classList.add("hide");
        document.querySelector("body").style.overflow = "scroll";
      }, 1000);
    }
  }, [headerIsLoaded]);*/

  useEffect(() => {
    async function getImageOfTheDay() {
      try {
        const response = await fetch(
          "https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG"
        );
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return await response.json();
      } catch (err) {
        console.log(err);
      }
    }
    getImageOfTheDay().then((data) => {
      if (data !== undefined) {
        if (data.media_type !== "video") {
          setImageOfTheDay(data);
        }
        setHeaderIsLoaded(true);
      }
    });
  }, []);

  const styles = {
    headerHero: {
      backgroundImage: `url(${imageOfTheDay.url}`,
      backgroundSize: "cover",
      backgroundPosition: "center",
    },
  };

  return (
    <div className="home-container">
      <div className="home-image-container" style={styles.headerHero}>
        <div className="home-image-container--text-container">
          <img
            className="home-image-container--text-container_logo"
            src="/images/nasa-logo.png"
            alt="Nasa Logo"
          />
          <div className="home-image-container--text_text">
            <span>Images From Space</span>
          </div>
        </div>
        <div className="home-image-container--text-container_bg">
          <div className="home-image-container--text-container_bg-bar"></div>
        </div>
        <div className="home-image-container--text-container_note">
          <span>
            {imageOfTheDay.title} - {imageOfTheDay.date}
          </span>
        </div>
      </div>
    </div>
  );
};

export default Home;
