import React from 'react';
import '../css/Header.css';

async function getImageOfTheDay() {
  try {
    let response = await fetch('https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG');
    return await response.json();
  } catch (error) {
    console.error(error);
  }
}

getImageOfTheDay().then((data) => console.log(data));

const heroImage = getImageOfTheDay().then((data) => data);

const Header = props => {
  return (
    <header className="App-header">
      <div className="header-hero" style={{backgroundImage: `url(${heroImage.url}`}}>
        <div className="header-hero--text">
          <span>NASA Images</span>
        </div>
      </div>
    </header>
  );
}

export default Header;
