export function isEmptyObj(obj) {
  for (var key in obj) {
    if (obj.hasOwnProperty(key)) return false;
  }
  return true;
}

export function isEmptyArray(arr) {
  console.log("Array?", Array.isArray(arr));
  console.log("Array?", arr.length);
  if (Array.isArray(arr) && arr.length) {
    return false;
  } else {
    return true;
  }
}
