const GalleryImage = (props) => {
  const imageObj = props.imageObj;
  const imageId = imageObj.id;
  const imageSrc = imageObj.img_src;
  const fullName = imageObj.camera.full_name;
  const imageDate = imageObj.earth_date;

  return (
    <div className="gallery-image-container" data-image-id={imageId}>
      <div className="gallery-image">
        <img
          className={`grid-item grid-item-${props.index}`}
          src={imageSrc}
          alt={`${fullName}-${imageDate}`}
        />
        <div className="image-info-container">
          <p>{fullName}</p>
          <p>Date: {imageDate}</p>
        </div>
      </div>
    </div>
  );
};

export default GalleryImage;
