import './css/App.css';
import { Button } from '@shopify/polaris';
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import heroImage from './images/stars.jpg';

function App() {
  const [imageOfTheDay, setImageOfTheDay] = useState(heroImage);

  useEffect(() => {
    async function getImageOfTheDay() {
      try {
        const response = await fetch('https://api.nasas.gov/planetary/apod');
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return await response.json();
      } catch(err) {
        console.log(err);
      }
    }

    getImageOfTheDay().then((data) => {
      if (data !== undefined) {
        setImageOfTheDay(data);
      }
    });
  }, [])

  return (
    <div className="App">
      <Header imageOfTheDay={imageOfTheDay} />
    </div>
  );
}

export default App;
