import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  images: [],
  ids: [],
};

const likesSlice = createSlice({
  name: "likes",
  initialState,
  reducers: {
    likeAdded(state, action) {
      state.images.push(action.payload.images);
      state.ids.push(action.payload.id);
    },
  },
});

export default likesSlice.reducer;
