import "../css/DiamondGallery.css";
import GalleryImage from "./GaleryImage";
import { useState, useEffect } from "react";
import Pagination from "react-pagination-js";
import "react-pagination-js/dist/styles.css";

const Gallery = (props) => {
  const [galleryImages, setGalleryImages] = useState("unloaded");
  const [totalImages, setTotalImages] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    console.log("Ran a update in Gallery");
    async function getTotalImages() {
      try {
        const response = await fetch(props.galleryUrl);
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return await response.json();
      } catch (err) {
        console.log(err);
      }
    }
    getTotalImages().then((data) => {
      if (data !== undefined) {
        setTotalImages(data.photos.length);
      }
    });

    async function getGalleryImages() {
      try {
        const response = await fetch(`${props.galleryUrl}&page=${currentPage}`);
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return await response.json();
      } catch (err) {
        console.log(err);
      }
    }
    getGalleryImages().then((data) => {
      if (data !== undefined) {
        setGalleryImages(data.photos);
      }
    });
  }, [currentPage, props.galleryUrl]);

  const changeCurrentPage = (numPage) => {
    setCurrentPage(numPage);
  };

  if (galleryImages === "unloaded") {
    return <div>No Images Found</div>;
  }

  return (
    <>
      <div className="gallery-container">
        <div className="wrapper">
          {galleryImages.map((image, index) => (
            <GalleryImage key={index} index={index} imageObj={image} />
          ))}
        </div>
      </div>
      <Pagination
        currentPage={currentPage}
        changeCurrentPage={changeCurrentPage}
        totalSize={totalImages}
        sizePerPage={25}
      />
      <h2>current Page:{currentPage}</h2>
    </>
  );
};

export default Gallery;
