import React, { useEffect, useState, useMemo } from "react";
import "./css/app.css";
import Gallery from "./components/Gallery";
import Home from "./components/Home";
import { Route, HashRouter, Switch, Redirect } from "react-router-dom";

function App() {
  const [linkList, setLinkList] = useState([]);
  const links = useMemo(
    () => [
      {
        link: "/",
        path: "/",
        name: "Home",
        component: <Home links={links} />,
        exact: true,
      },
      {
        link: "/gallery/mars-rover",
        path: "/gallery/:gallery",
        name: "Mars Rover",
        component: <Gallery links={links} />,
        exact: false,
      },
      {
        link: "/gallery/apod",
        path: "/gallery/:gallery",
        name: "APOD",
        component: <Gallery links={links} />,
        exact: false,
      },
      {
        link: "/gallery/lucky",
        path: "/gallery/:gallery",
        name: "Feeling Lucky",
        component: <Gallery links={links} />,
        exact: false,
      },
      {
        link: "/gallery/loved",
        path: "/gallery/:gallery",
        name: "Loved Pics",
        component: <Gallery links={links} />,
        exact: false,
      },
    ],
    [links]
  );

  useEffect(() => {
    setLinkList(links);
  }, []);

  return (
    <div className="App">
      <HashRouter>
        <Switch>
          {links.map((obj, index) => {
            return obj.exact ? (
              <Route key={index} exact path={obj.path}>
                {obj.component}
              </Route>
            ) : (
              <Route key={index} path={obj.path}>
                {obj.component}
              </Route>
            );
          })}
          <Redirect to="/" />
        </Switch>
      </HashRouter>
    </div>
  );
}

export default App;
