import "../css/Gallery.css";
import GalleryImage from "./GaleryImage";
import { useState, useEffect } from "react";
import Pagination from "react-pagination-js";
import "react-pagination-js/dist/styles.css";
import Select from "./Select";

const Gallery = (props) => {
  const [galleryImages, setGalleryImages] = useState("unloaded");
  const [totalImages, setTotalImages] = useState(0);
  const [totalPages, setTotalPages] = useState();
  const [currentPage, setCurrentPage] = useState(1);
  const [sizePerPage, setSizePerPage] = useState(10);
  const [startPage, setStartPage] = useState(1);
  const [endPage, setEndPage] = useState(10);

  useEffect(() => {
    console.log("Updated the Gallery");
    async function getTotalImages() {
      try {
        const response = await fetch(props.galleryUrl);
        if (!response.ok) {
          throw new Error(response.statusText);
        }
        return await response.json();
      } catch (err) {
        console.log(err);
      }
    }
    getTotalImages().then((data) => {
      if (data !== undefined) {
        setTotalImages(data.photos.length);
        setGalleryImages(data.photos);
        console.log("Data: ", data.photos);
      }
    });
  }, [props.galleryUrl]);

  useEffect(() => {
    console.log("Reloaded the Gallery");
  }, [currentPage, sizePerPage]);

  const getTotalPages = () => {
    setTotalPages(Math.ceil(totalImages / sizePerPage));
  };

  const selectNumImages = (num) => {
    console.log("num: ", num);
    setSizePerPage(num);
  };

  const changeCurrentPage = (numPage) => {
    setCurrentPage(numPage);
  };

  if (galleryImages === "unloaded") {
    return <div>No Images Found</div>;
  }

  return (
    <>
      <Select selectNumImages={selectNumImages} />
      <div className="grid-container">
        {galleryImages
          .slice(startPage - 1, startPage + sizePerPage - 1)
          .map((image, index) => (
            <GalleryImage key={index} index={index} imageObj={image} />
          ))}
      </div>
      <Pagination
        currentPage={currentPage}
        changeCurrentPage={changeCurrentPage}
        totalSize={totalImages}
        sizePerPage={sizePerPage}
        theme="dark"
      />
      <h2>current Page:{currentPage}</h2>
    </>
  );
};

export default Gallery;
