const Loading = ({ isLoading }) => {
  const loadingCheck = () => {
    if (!isLoading) {
      document
        .querySelector(".gallery-loading-container")
        .classList.add("hide");
    }
  };
  loadingCheck();
  return (
    <div className="gallery-loading-container">
      <h1>LOADING...</h1>
    </div>
  );
};

export default Loading;
