import './css/App.css';
import { Button } from '@shopify/polaris';
import React, { useState, useEffect } from 'react';
import Header from './components/Header';

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [imageOfTheDay, setImageOfTheDay] = useState();

  useEffect(() => {
    async function getImageOfTheDay() {
      try {
        let response = await fetch('https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG');
        return await response.json();
      } catch (error) {
        console.error(error);
      }
    }

    getImageOfTheDay().then((data) => {
      console.log(data);
      if (data) {
        setIsLoaded(true);
        setImageOfTheDay(data);
        console.log(imageOfTheDay);
      }
    });
  })

  return (
    <div className="App">
      {isLoaded
        ? <Header imageOfTheDay={imageOfTheDay} />
        : <Header imageOfTheDay={'./images/stars.jpg'} />
      }
    </div>
  );
}

export default App;
