import { useSelector, useDispatch } from "react-redux";
import { likeAdded, likeRemoved } from "./likes/likesSlice.js";

const GalleryImage = (props) => {
  let imageTemplate = {};
  const likeInfo = useSelector((state) => state.likes);
  const dispatch = useDispatch();

  if (props.galleryName === "mars-rover") {
    imageTemplate.id = props.imageObj.id.toString();
    imageTemplate.src = props.imageObj.img_src;
    imageTemplate.title = `Rover Name: ${props.imageObj.rover.name}`;
    imageTemplate.date = props.imageObj.earth_date;
    imageTemplate.media_type = props.imageObj.media_type
      ? props.imageObj.media_type
      : "image";
  } else if (props.galleryName === "apod" || props.galleryName === "lucky") {
    const validateHQ =
      props.highqtyImage &&
      props.imageObj.hdurl !== "" &&
      props.imageObj.hdurl !== undefined
        ? props.imageObj.hdurl
        : props.imageObj.url;

    imageTemplate.id = `${props.imageObj.title.replace(/ /g, "-")}-${
      props.imageObj.date
    }`;
    imageTemplate.src = validateHQ;
    imageTemplate.title = props.imageObj.title;
    imageTemplate.date = props.imageObj.date;
    imageTemplate.media_type = props.imageObj.media_type;
  } else {
    imageTemplate.id = props.imageObj.id;
    imageTemplate.src = props.imageObj.src;
    imageTemplate.title = props.imageObj.title;
    imageTemplate.date = props.imageObj.date;
    imageTemplate.media_type = props.imageObj.media_type
      ? props.imageObj.media_type
      : "image";
  }

  const Img = (props) => {
    return (
      <a href={imageTemplate.src}>
        <img
          className="grid-item"
          src={imageTemplate.src}
          alt={`${imageTemplate.title}-${imageTemplate.date}`}
        />
      </a>
    );
  };

  const Video = ({ imageObj }) => {
    return (
      <a
        href={imageTemplate.src}
        srl_video_thumbnail="/images/video-placeholder.jpeg"
        srl_video_caption={imageObj.explanation}
        srl_video_scale="50"
      >
        <img
          className="grid-item"
          src="/images/video-placeholder.jpeg"
          alt={`${imageTemplate.title}-${imageTemplate.date}`}
        />
      </a>
    );
  };

  const Media = ({ imageObj }) => {
    if (imageObj.media_type === "video") {
      return <Video imageObj={imageObj} />;
    } else {
      return <Img />;
    }
  };

  const likeImageClickHandler = (event) => {
    console.log("Click");
    const imageEl = event.target.closest(".gallery-image");
    const image = {
      id: imageEl.dataset.mediaId,
      src: imageEl.dataset.mediaSrc,
      title: imageEl.dataset.mediaTitle,
      date: imageEl.dataset.mediaDate,
      media_type: imageEl.dataset.mediaType,
    };

    if (event.target.checked) {
      dispatch(
        likeAdded({
          image: image,
          id: image.id,
        })
      );
    } else {
      if (props.galleryState.total === 1) {
        props.updateGalleryStateTotal(0);
      }
      const newFaveImages = likeInfo.images.filter(
        (currentImg) => currentImg.id !== image.id
      );
      const newFaveIds = likeInfo.ids.filter((id) => id !== image.id);

      dispatch(
        likeRemoved({
          images: newFaveImages,
          ids: newFaveIds,
        })
      );
    }
  };

  const LikeHeart = () => {
    let needsChecked = false;

    const checkIfLiked = () => {
      if (likeInfo.ids.includes(imageTemplate.id)) {
        needsChecked = true;
      }
    };
    checkIfLiked();

    return (
      <label className="heart-switch">
        {needsChecked ? (
          <input checked type="checkbox" onChange={likeImageClickHandler} />
        ) : (
          <input type="checkbox" onChange={likeImageClickHandler} />
        )}
        <svg viewBox="0 0 33 23" fill="white">
          <path d="M23.5,0.5 C28.4705627,0.5 32.5,4.52943725 32.5,9.5 C32.5,16.9484448 21.46672,22.5 16.5,22.5 C11.53328,22.5 0.5,16.9484448 0.5,9.5 C0.5,4.52952206 4.52943725,0.5 9.5,0.5 C12.3277083,0.5 14.8508336,1.80407476 16.5007741,3.84362242 C18.1491664,1.80407476 20.6722917,0.5 23.5,0.5 Z"></path>
        </svg>
      </label>
    );
  };

  return (
    <div className="gallery-image-container">
      <div
        className="gallery-image"
        data-media-id={imageTemplate.id}
        data-media-src={imageTemplate.src}
        data-media-alt={`${imageTemplate.title}-${imageTemplate.date}`}
        data-media-title={imageTemplate.title}
        data-media-date={imageTemplate.date}
        data-media-type={imageTemplate.media_type}
      >
        <Media imageObj={props.imageObj} />
        <div className="image-info-container">
          <div className="image-info-container--text">
            <p>{imageTemplate.title}</p>
            <p>Earth Date: {imageTemplate.date}</p>
          </div>
          <div className="image-info-container--like">
            <LikeHeart />
          </div>
        </div>
      </div>
    </div>
  );
};

export default GalleryImage;
