import './css/App.css';
import { Button } from '@shopify/polaris';
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import heroImage from './images/stars.jpg';

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [imageOfTheDay, setImageOfTheDay] = useState(heroImage);

  useEffect(() => {
    async function getImageOfTheDay() {
      try {
        let response = await fetch('https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG');
        return await response.json();
      } catch (error) {
        console.error(error);
      }
    }

    getImageOfTheDay().then((data) => {
      if (data.length) {
        setIsLoaded(true);
        setImageOfTheDay(data.url);
      }
    });
  })

  return (
    <div className="App">
      <Header imageOfTheDay={imageOfTheDay} />
    </div>
  );
}

export default App;
