import LikeHeart from "./LikeHeart";
import {
  createMarsImage,
  createApodImage,
  createDefaultImage,
} from "../functions/createImages";

const Img = ({ image }) => {
  return (
    <a href={image.src}>
      <img
        className="grid-item"
        src={image.src}
        alt={`${image.title}-${image.date}`}
      />
    </a>
  );
};

const Video = ({ image }) => {
  return (
    <a
      href={image.src}
      srl_video_thumbnail="/images/video-placeholder.jpeg"
      srl_video_caption={image.explanation}
      srl_video_scale="50"
    >
      <img
        className="grid-item"
        src="/images/video-placeholder.jpeg"
        alt={`${image.title}-${image.date}`}
      />
    </a>
  );
};

const Media = ({ image }) => {
  if (image.media_type === "video") {
    return <Video image={image} />;
  } else {
    return <Img image={image} />;
  }
};

const GalleryImage = (props) => {
  let imageTemplate = {};

  if (props.galleryName === "mars-rover") {
    imageTemplate = createMarsImage(props.image);
  } else if (props.galleryName === "apod" || props.galleryName === "lucky") {
    imageTemplate = createApodImage(props.image, props.hqImage);
  } else {
    imageTemplate = createDefaultImage(props.image);
  }

  console.log("Template", imageTemplate);

  const likeImageClickHandler = (event) => {
    const imageEl = event.target.closest(".gallery-image");
    const image = {
      id: imageEl.dataset.mediaId,
      src: imageEl.dataset.mediaSrc,
      title: imageEl.dataset.mediaTitle,
      date: imageEl.dataset.mediaDate,
      media_type: imageEl.dataset.mediaType,
    };

    if (event.target.checked) {
      props.updateFave(image, "add");
    } else {
      console.log("Unlike", props.faveGallery);
      const newFaveImages = props.faveGallery.filter(
        (currentImg) => currentImg.id !== image.id
      );
      const newFaveIds = props.faveIds.filter((id) => id !== image.id);

      if (props.galleryName === "loved") {
        props.resetGalleryUrl("Reset");
      }
      props.updateFave({ images: newFaveImages, ids: newFaveIds }, "remove");
    }
  };

  return (
    <div className="gallery-image-container">
      <div
        className="gallery-image"
        data-media-id={props.image.id}
        data-media-src={props.image.src}
        data-media-alt={`${props.image.title}-${props.image.date}`}
        data-media-title={props.image.title}
        data-media-date={props.image.date}
        data-media-type={props.image.media_type}
      >
        <Media image={props.image} />
        <div className="image-info-container">
          <div className="image-info-container--text">
            <p>{props.image.title}</p>
            <p>Earth Date: {props.image.date}</p>
          </div>
          <div className="image-info-container--like">
            <LikeHeart
              liked={props.liked}
              likeImageClickHandler={likeImageClickHandler}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default GalleryImage;
