import "../css/mobileNav.css";

const MobileNav = () => {
  let x1, y1, timeStart, timeEnd;
  const navDrawer = document.querySelector("mobile-nav--container");
  const drawerHeight = navDrawer.clientHeight;

  const addCloseEventListeners = (el) => {
    navDrawer.addEventListener("touchstart", closeStart);
    navDrawer.addEventListener("touchmove", closeMove);
    navDrawer.addEventListener("touchend", closeEnd);
  };

  const removeCloseEventListeners = (el) => {
    navDrawer.removeEventListener("touchstart", closeStart);
    navDrawer.removeEventListener("touchmove", closeMove);
    navDrawer.removeEventListener("touchend", closeEnd);
  };

  const addOpenEventListeners = (el) => {
    navDrawer.addEventListener("touchstart", openStart);
    navDrawer.addEventListener("touchmove", openMove);
    navDrawer.addEventListener("touchend", openEnd);
  };

  const removeOpenEventListeners = (el) => {
    navDrawer.removeEventListener("touchstart", openStart);
    navDrawer.removeEventListener("touchmove", openMove);
    navDrawer.removeEventListener("touchend", openEnd);
  };

  const openStart = (event) => {
    let touchLocation = target.targetTouches[0];
    x1 = touchLocation.clientX;
    y1 = touchLocation.clientY;
    timeStart = event.timeStamp;

    navDrawer.style.transition = "";
  };

  const openMove = (event) => {
    let yLocation = event.touches[0].clientY;
    let xLocation = event.touches[0].clientX;

    let yDiff = Math.abc(y1 - yLocation);
    let xDiff = Math.abc(x1 - xLocation);

    if (yLocation > drawerHeight + y1) {
      yLocation = drawerHeight + y1;
    }

    if (yDiff > xDiff) {
      document.body.style.overflow = "hidden";

      navDrawer.style.bottom = `${yLocation - (drawerHeight + y1)}px`;
    }
  };

  const openEnd = (event) => {
    let y2 = event.changedTouches[0].clientY;
    timeEnd = event.timeStamp;
    let timeDiff = timeEnd - timeStart;
    let yDiff = y2 - y1;

    if (yDiff > drawerHeight / 4 || (timeDiff < 200 && yDiff > 19)) {
      openMenu(navDrawer);
    } else {
      closeMenu(navDrawer);
    }
    navDrawer.style.bottom = null;
  };

  const closeStart = (event) => {
    let touchLocation = event.targetTouches[0];
    x1 = touchLocation.clientX;
    y1 = touchLocation.clientY;
    timeStart = event.timeStamp;
  };

  const closeMove = (event) => {
    let touchLocation = event.touches[0];
    let yLocation = touchLocation.clientY;

    console.log(yLocation);

    if (yLocation > drawerHeight + y1) {
      yLocation = drawerHeight + y1;
    }

    navDrawer.classList.remove("active");
    navDrawer.style.transition = "";

    let marker = yLocation - y1;
    if (marker > 0) {
      marker = 0;
    }
    navDrawer.style.bottom = `${marker}px`;
  };

  const closeEnd = (event) => {
    let touchLocation = event.changedTouches[0];
    let y2 = touchLocation.clientY;
    let yDiff = y1 - y2;
    timeEnd = event.timeStamp;
    timeDiff = timeEnd - timeStart;

    if (yDiff > drawerHeight / 3 || timeDiff < 100) {
      console.log("closing");
      closeMenu(navDrawer);
    } else {
      console.log("opening");
      openMenu(navDrawer);
    }
  };

  const openMenu = (el) => {
    el.classList.add("active");

    navDrawer.style.bottom = null;
    document.body.style.overflow = "hidden";

    addCloseEventListeners(navDrawer);
    removeOpenEventListeners(navDrawer);
  };

  const closeMenu = (el) => {
    navDrawer.style.transition = "0.2s";

    navDrawer.style.bottom = null;
    el.classList.remove("avtive");

    document.body.style.overflow = "scroll";

    removeCloseEventListeners(navDrawer);
    addOpenEventListeners(navDrawer);
  };

  return (
    <div
      className="mobile-nav--container"
      onTouchStart={openStart}
      onTouchMove={openMove}
      onTouchEnd={openEnd}
    ></div>
  );
};

export default MobileNav;
