import { NavLink } from "react-router-dom";
import { useParams } from "react-router-dom";

const Navigation = ({ links }) => {
  const params = useParams();
  const likedIds = window.localStorage.getItem("favorites-ids");
  let noLikes;

  if (likedIds === null) {
    noLikes = true;
  } else if (likedIds.length === 0) {
    noLikes = true;
  } else {
    noLikes = false;
  }

  const linkValidation = (obj, index) => {
    console.log(params);
    if (params.length === 0 || (noLikes && obj.link === "/gallery/loved")) {
      return;
    } else {
      return (
        <NavLink key={index} to={obj.link}>
          {obj.name}
        </NavLink>
      );
    }
  };

  return (
    <div className="main-nav--container">
      <div className="main-nav--links">
        {links.map((obj, index) => {
          return linkValidation(obj, index);
        })}
      </div>
    </div>
  );
};

export default Navigation;
