import './css/App.css';
import { Button } from '@shopify/polaris';
import React, { useState, useEffect } from 'react';
import Header from './components/Header';

function App() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [imageOfTheDay, setImageOfTheDay] = useState();

  useEffect(() => {
    async function getImageOfTheDay() {
      try {
        let response = await fetch('https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG');
        return await response.json();
      } catch (error) {
        console.error(error);
      }
    }

    getImageOfTheDay().then((data) => {
      setIsLoaded(true);
      setImageOfTheDay(data);
    });
  })

  if (isLoaded) {
    headerComp = <Header imageOfTheDay={imageOfTheDay} />;
  } else {
    headerComp = <Header />
  }

  return (
    <div className="App">
      {headerComp}
    </div>
  );
}

export default App;
