import "../css/mobileNav.css";
import { useState, useEffect } from "react";

const MobileNav = () => {
  const [navDrawer, setNavDrawer] = useState();
  const [drawerHeight, setDrawerHeight] = useState();

  useEffect(() => {
    getDrawer();
  }, []);

  useEffect(() => {
    if (navDrawer !== undefined) {
      getDrawerHeight();
    }
  }, [navDrawer]);

  const getDrawer = () => {
    setNavDrawer(document.querySelector(".mobile-nav--container"));
  };
  const getDrawerHeight = () => {
    setDrawerHeight(navDrawer.clientHeight);
  };

  return (
    <>
      <div className="mobile-nav--container"></div>
    </>
  );
};

export default MobileNav;
