import "./css/App.css";
import Header from "./components/Header";
import Gallery from "./components/Gallery";
import Navigation from "./components/Navigation";
import Home from "./components/Home";
import { Route, HashRouter, Switch } from "react-router-dom";

function App() {
  const links = [
    {
      link: "/",
      path: "/",
      name: "Home",
      component: <Home />,
      exact: true,
    },
    {
      link: "/gallery?gallery=mars-rover",
      path: "/gallery/:gallery",
      name: "Mars Rover",
      component: (
        <Gallery
          url={
            "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG"
          }
        />
      ),
      exact: false,
    },
    {
      link: "/gallery?gallery=apod",
      path: "/gallery/:gallery",
      name: "APOD",
      component: (
        <Gallery
          url={
            "https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG&start_date=2021-09-01"
          }
        />
      ),
      exact: false,
    },
  ];

  return (
    <div className="App">
      <Header />
      <HashRouter>
        <Navigation links={links} />
        <Switch>
          {links.map((obj, index) => {
            return obj.exact ? (
              <Route key={index} exact path={obj.path}>
                {obj.component}
              </Route>
            ) : (
              <Route key={index} path={obj.path}>
                {obj.component}
              </Route>
            );
          })}
        </Switch>
      </HashRouter>
    </div>
  );
}

export default App;
