import "./css/App.css";
import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import Gallery from "./components/Gallery";
import heroImage from "./images/stars.jpg";
import Pagination from "react-pagination-js";
import "react-pagination-js/dist/styles.css";

function App() {
  const [imageOfTheDay, setImageOfTheDay] = useState({ url: heroImage });
  const [galleryImages, setGalleryImages] = useState("unloaded");
  const [totalImages, setTotalImages] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    getTotalImages().then((data) => {
      if (data !== undefined) {
        setTotalImages(data.photos.length);
      }
    });

    getImageOfTheDay().then((data) => {
      if (data !== undefined) {
        setImageOfTheDay(data);
      }
    });

    getGalleryImages().then((data) => {
      if (data !== undefined) {
        setGalleryImages(data.photos);
      }
    });
  }, []);

  async function getTotalImages() {
    try {
      const response = await fetch(
        "https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG"
      );
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return await response.json();
    } catch (err) {
      console.log(err);
    }
  }

  async function getImageOfTheDay() {
    try {
      const response = await fetch(
        "https://api.nasa.gov/planetary/apod?api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG"
      );
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return await response.json();
    } catch (err) {
      console.log(err);
    }
  }

  async function getGalleryImages() {
    try {
      const response = await fetch(
        `https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?sol=1000&api_key=KnTfeP68Y6KMuMuhCMWSxjlYXsqjgoCWUo8chunG&page=${currentPage}`
      );
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return await response.json();
    } catch (err) {
      console.log(err);
    }
  }

  const changeCurrentPage = (numPage) => {
    setCurrentPage(numPage);

    getGalleryImages().then((data) => {
      if (data !== undefined) {
        setGalleryImages(data.photos);
      }
    });
  };

  return (
    <div className="App">
      <Header imageOfTheDay={imageOfTheDay} />
      <Gallery
        galleryImages={galleryImages}
        currentPage={currentPage}
        changeCurrentPage={changeCurrentPage}
        totalPages={totalImages}
      />
      <Pagination
        currentPage={currentPage}
        changeCurrentPage={changeCurrentPage}
        totalSize={totalImages}
        sizePerPage={25}
      />
      <h2>current Page:{currentPage}</h2>
    </div>
  );
}

export default App;
